INSERT INTO "EMR"."FORM_TEMPLATE_STORAGE" VALUES ('bef287260bb84af5a28df7e424f970d6', 'ryjl', '<div style="width: 210mm; display: none;" class="sql-wrapper"><div style="display: block;" name="yyxx" class="sql-node">
