        <span class="sql-result-type">single</span>
        <span style="display: none;" class="var-name"></span>
        <span style="display: none;" class="sql-result-limit"></span>

        <div class="sql">select a.*,(select trunc((select cyrq from cyjl where jzxh = (select jzxh from jzjl where brbh = ''${patientCode}'' and sfzk = 0)) - (select ryrq from (select * from brryxx where brbh = ''${patientCode}'' order by ryrq) where rownum = 1)) from dual) as sjzyts from brjbxx a where a.brbh=''${patientCode}''</div>
    </div><div style="display: block;" name="brryxx" class="sql-node">
        <span class="sql-name">brryxx</span>
        <span class="sql-result-type">single</span>
