        <span style="display: none;" class="sql-result-limit"></span>

        <div class="sql">select * from (select * from brryxx where brbh = ''${patientCode}'' order by ryrq desc) where rownum = 1</div>
    </div><div style="display: block;" name="ssjl" class="sql-node">
