(select xm from ygjbxx c where c.ygbh = a.sxys) as sxys_xm, 
(select xm from ygjbxx c where c.ygbh = a.bmy) as bmy_xm, 
(select xm from ygjbxx c where c.ygbh = a.zkys) as zkys_xm, 
