INSERT INTO "EMR"."FORM_TEMPLATE_STORAGE" VALUES ('f39b42096daa474eb201915dce143fad', 'ssjl', '<div class="sql-wrapper" style="width: 210mm; display: none;"><div class="sql-node" name="yyxx" style="display: block;">
