        <span class="sql-result-type">single</span>
        <span class="var-name" style="display: none;"></span>
        <span class="sql-result-limit" style="display: none;"></span>

        <div class="sql">SELECT
	ssjl.*, sszxx.xm AS SSZ_XM,
	zsxx.xm AS ZS_XM,
	hsxx.xm AS HS_XM,
        mzysxx.xm AS MZYS_XM,
        ssqmzxx.xm AS SSQMZ_XM
FROM
	NEW_SSJL ssjl
LEFT JOIN ygjbxx sszxx ON SSJL.SSZ = sszxx.YGBH
LEFT JOIN ygjbxx zsxx ON SSJL.ZS = zsxx.YGBH
LEFT JOIN ygjbxx hsxx ON SSJL.HS = zsxx.YGBH
LEFT JOIN ygjbxx mzysxx ON SSJL.MZYS = mzysxx.YGBH
